import React, {useState, useEffect, ReactDOM} from "react"




function Tasks(props){

    // all of my states 
    const [taskState, setTaskState] = useState([])

    const [inputValue, setInputValue] = useState('')  
    // logic

    const inputFunctionHandler = (event) => {
        setInputValue(event.target.value);
        console.log(inputValue)
      };
    const submitTask = (e) =>{
        e.preventDefault();
        let temp = taskState;
        temp.push(new task(inputValue))
        setTaskState(temp);
        setInputValue('');
        console.log(taskState);
        console.log(taskState[0])

    }
    const dropDownBtn = (e) => {
        e.preventDefault();
    }

    let taskDisplay = taskState.map(element => {
     return(  
        <div>
            <button >Delete</button>
            <li>{element.name}</li>
            <input className = "checkbox" type="checkbox" ></input>
        </div>
     )
    })
    // create a task logic
    function task(name) {
        this.name = name
        this.isComplete = false
      }

    // my JSX

    return (
        <div>
        <form>
            <input value = {inputValue} onChange={inputFunctionHandler} ></input>
            <button onClick = {submitTask}>Submit</button>
        </form>
        <div className="dropdown">
          <button className="dropbtn" onClick={dropDownBtn}>View</button>
          <div className="dropdown-content">
            <button>All</button>
            <button>Complete</button>
            <button>Incomplete</button>
          </div>
        </div>

        <div className="tasks">
            {taskDisplay}
        </div>
        </div>
    )
}



export default Tasks;
